import 'package:flutter/material.dart';import 'package:provider/provider.dart';import '../../common/extensions/extensions.dart';import '../../data/api_firebase/chat_provider.dart';import 'component/home_chat_appbar.dart';import 'component/home_chat_horizontal.dart';import 'component/home_chat_vertical.dart';class HomeScreen extends StatefulWidget {  const HomeScreen({Key? key}) : super(key: key);  @override  State<HomeScreen> createState() => _HomeScreenState();}class _HomeScreenState extends State<HomeScreen> {  @override  void initState() {    context.read<ChatProvider>().getAllUsers();    context.read<ChatProvider>().getProfile();    super.initState();  }  @override  void dispose() {    super.dispose();  }  @override  Widget build(BuildContext context) {    return Consumer<ChatProvider>(      builder: (context, data, child) {        final users = data.users;        return GestureDetector(          onTap: () => FocusScope.of(context).unfocus(),          child: SafeArea(            child: WillPopScope(              onWillPop: () {                return Future.value(true);              },              child: Scaffold(                backgroundColor: context.theme.colorScheme.background,                body: Column(                  children: [                    data.users.isNotEmpty ? HomeChatAppBar(user: data.userProfile) : const SizedBox(),                    data.users.isNotEmpty ? HomeChatHorizontal(users: users, user: data.userProfile) : const SizedBox(height: 82),                    const SizedBox(height: 30),                    HomeChatVertical(users),                  ],                ),              ),            ),          ),        );      },    );  }}